function ajaxify(e){$.ajax({url:e,data:{ajax:1},beforeSend:function(){$(".frame").addClass("collapsed")},success:function(e){$("body, html").animate({scrollTop:0},1e3);$("#main").fadeOut(500,function(){$("#main").empty().append(e).fadeIn(500,function(){$("a.ajax","#main").on("click",function(e){e.preventDefault();ajaxify($(this).attr("href"))})})})},error:function(){}});history.pushState({},"",e)}$(document).ready(function(){$("#secondary, .site-title").hover(function(){$(".frame").removeClass("collapsed")},function(){$(".frame").addClass("collapsed")});$("a.ajax").on("click",function(e){e.preventDefault();$("li","#secondary").removeClass("active");$(this).parent().addClass("active");ajaxify($(this).attr("href"))})});